import { Component, EventEmitter, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-pop-modal',
  templateUrl: './pop-modal.component.html',
  styleUrls: ['./pop-modal.component.css']
})


export class PopModalComponent implements OnInit {
  @Output() dayTypeSelected = new EventEmitter<string>();
  ngOnInit(): void {

  }


  selectDayType(dayType: string): void {
    this.dayTypeSelected.emit(dayType);
  }

  onOpen(flag : boolean){
    if(flag == true){
         flag == true;
         console.log("Open is working");
    }
  }

  onClose(flag : boolean){
    if(flag == false){
      flag == false;
      console.log("Close is working");

    }
  }

}
<div class="modal-overlay">
  <div class="modal-container">
    <h2>Select Day Type</h2>
    <div class="options">
      <button (click)="selectDayType('working')">Working Day</button>
      <button (click)="selectDayType('nonWorking')">Non-Working Day</button>
    </div>
  </div>
</div>
