import { DatePipe } from '@angular/common';
import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-pop-modal',
  templateUrl: './pop-modal.component.html',
  styleUrls: ['./pop-modal.component.css']
})



export class PopModalComponent implements OnInit {

  todayDate: string | any;

  constructor() {
    //const currentDate = new Date();
    this.todayDate = new Date();
  }
  ngOnInit(): void {

  }


}
<div class="modal-overlay">
  <div class="modal-container">
    <span class="cancel-icon">&times;</span>
    <h2 class="modal-title">Select Day Type</h2>
    <div class="modal-date">{{ todayDate | date }}</div>
    <div class="options">
      <a href="/workDay">Working Day</a>
      <a href="/non-workDay">Non-Working Day</a>
    </div>
  </div>
</div>
