import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-pop-modal',
  templateUrl: './pop-modal.component.html',
  styleUrls: ['./pop-modal.component.css']
})


export class PopModalComponent implements OnInit{

  ngOnInit(): void {

  }

  onOpen(flag : boolean){
    if(flag == true){
         flag == true;
         console.log("Open is working");
    }
  }

  onClose(flag : boolean){
    if(flag == false){
      flag == false;
      console.log("Close is working");

    }
  }

}
<div class="container">
    <div class="col-sm-6 my-10">
        <h1>Pop Page</h1>
        <button (click)="onOpen(true)"><div>  <h3>Hello Open</h3></div></button>
        <button (click)="onClose(false)"> <div>  <h3>Hello Close</h3></div></button>

    </div>
</div>


<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<style>
  body {
    font-family: Arial, sans-serif;
  }
  .modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    justify-content: center;
    align-items: center;
  }
  .modal-content {
    background-color: white;
    padding: 20px;
    border-radius: 5px;
    text-align: center;
  }
</style>
</head>
<body>

<button id="openModal">Open Modal</button>

<div id="myModal" class="modal">
  <div class="modal-content">
    <p>Today's Date: <span id="currentDate"></span></p>
    <p>Current Time (IST): <span id="currentTime"></span></p>
    <p>Today is <span id="currentDay"></span></p>
    <p>Select an option:</p>
    <button id="workingDay">Working Day EOD</button>
    <button id="holiday">Today is Holiday</button>
  </div>
</div>

<script>
  const modal = document.getElementById('myModal');
  const openModalBtn = document.getElementById('openModal');
  const currentDateSpan = document.getElementById('currentDate');
  const currentTimeSpan = document.getElementById('currentTime');
  const currentDaySpan = document.getElementById('currentDay');
  const workingDayBtn = document.getElementById('workingDay');
  const holidayBtn = document.getElementById('holiday');

  openModalBtn.addEventListener('click', () => {
    const now = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit', timeZoneName: 'short' };
    const formattedDate = now.toLocaleDateString('en-US', options);
    currentDateSpan.textContent = formattedDate;

    const istOptions = { timeZone: 'Asia/Kolkata', ...options };
    const formattedISTTime = now.toLocaleDateString('en-US', istOptions);
    currentTimeSpan.textContent = formattedISTTime;

    currentDaySpan.textContent = now.toLocaleDateString('en-US', { weekday: 'long' });

    modal.style.display = 'flex';
  });

  workingDayBtn.addEventListener('click', () => {
    // Handle Working Day EOD option
    modal.style.display = 'none';
  });

  holidayBtn.addEventListener('click', () => {
    // Handle Today is Holiday option
    modal.style.display = 'none';
  });
</script>

</body>
</html>
