import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-pop-modal',
  templateUrl: './pop-modal.component.html',
  styleUrls: ['./pop-modal.component.css']
})



export class PopModalComponent implements OnInit {

  @Output() dayTypeSelected = new EventEmitter<string>();

  constructor(private router: Router) {}

  ngOnInit(): void {

  }

  selectDayType(dayType: string): void {
    console.log(dayType)
    if (dayType === 'workDay') {
      this.router.navigateByUrl('view');
    } else if (dayType === 'non-workDay') {
      this.router.navigateByUrl('/pop/non-workDay');
    }
  }
}
<div class="modal-overlay">
  <div class="modal-container">
    <h2>Select Day Type</h2>
    <div class="options">
      <button (click)="selectDayType('workDay')">Working Day</button> &nbsp;
      <button (click)="selectDayType('non-workDay')">Non-Working Day</button>
    </div>
  </div>
</div>
