import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-pop-modal',
  templateUrl: './pop-modal.component.html',
  styleUrls: ['./pop-modal.component.css']
})


export class PopModalComponent implements OnInit{

  ngOnInit(): void {

  }

  onOpen(flag : boolean){
    if(flag == true){
         flag == true;
         console.log("Open is working");
    }
  }

  onClose(flag : boolean){
    if(flag == false){
      flag == false;
      console.log("Close is working");

    }
  }

}
<div class="container">
    <div class="col-sm-6 my-10">
        <h1>Pop Page</h1>
        <button onclick="onOpen(true)"><div>  <h3>Hello Open</h3></div></button>
        <button onclick="onClose(false)"> <div>  <h3>Hello Close</h3></div></button>

    </div>
</div>
