import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Eod } from '../Model/eod';
import { EodService } from '../Service/eod.service';

@Component({
  selector: 'app-leave-eod',
  templateUrl: './leave-eod.component.html',
  styleUrls: ['./leave-eod.component.css']
})

export class LeaveEODComponent implements OnInit {
  leaveTypes: string[] = [ ' ', 'Sick', 'Plan', 'Bank'];

  nonworkDayForm : FormGroup;
  eodModel : Eod[] | any;
  id : string | any;
  workTypes: ['Bug', 'Task', 'User Story'] | any;

 constructor(private fb : FormBuilder, private eodService : EodService, private route : ActivatedRoute, private router : Router){
   this.nonworkDayForm = this.fb.group({
    leaveType : [null, [Validators.required]],
     note : [null, [Validators.required]],
     date : [new Date(), [Validators.required]]
   });
 }
  ngOnInit(){

  }

  onNonWorkDayAddNew(){

  }


}
<div class="container">
    <div class="col-sm-10"></div>
    <h3 class="offset-sm-5">Non-Work Day EOD</h3>
    <hr>
    <div class="form-container">
        <form #form="ngForm" [formGroup]="nonworkDayForm" (ngSubmit)="onNonWorkDayAddNew()">
            <div class="mb-3 col-sm-6 offset-sm-3">
                <label for="leaveType" class="form-label">Leave Type</label>
                <select class="dropdown" class="form-control" formControlName="leaveType">
                    <option *ngFor="let item of leaveTypes">{{item}}</option>
                </select>
                <!-- <input type="text"  > -->
                <div *ngIf="nonworkDayForm.controls['leaveType'].errors && (form.submitted || nonworkDayForm .controls['leaveType'].dirty)">
                    <small [hidden]="!nonworkDayForm.controls['leaveType'].errors['required']" class="text-danger">
                        Please Select Leave Type
                    </small>
                </div>
            </div>

            <div class="mb-3 col-sm-6 offset-sm-3">
                <label for="date" class="form-label">Date</label>
                <input type="date" class="form-control" formControlName="date">
                <div *ngIf="nonworkDayForm.controls['date'].errors && (form.submitted || nonworkDayForm.controls['date'].dirty)">
                    <small [hidden]="!nonworkDayForm.controls['date'].errors['required']" class="text-danger">
                        Please Select Date
                    </small>
                </div>
            </div>

            <div class="mb-3 col-sm-6 offset-sm-3">
                <label for="note" class="form-label">Note</label>
                <input type="text" class="form-control" formControlName="note">
                <div *ngIf="nonworkDayForm.controls['note'].errors && (form.submitted || nonworkDayForm.controls['note'].dirty)">
                    <small [hidden]="!nonworkDayForm.controls['note'].errors['required']" class="text-danger">
                        Please Enter Note
                    </small>
                </div>
            </div>



            <button class="btn btn-success my-2 offset-sm-3" type="submit">Submit</button>
        </form>
</div>
