import { Component, OnInit } from '@angular/core';
import { Eod } from '../Model/eod';
import { EodService } from '../Service/eod.service';

@Component({
  selector: 'app-view-eod',
  templateUrl: './view-eod.component.html',
  styleUrls: ['./view-eod.component.css']
})
export class ViewEODComponent implements OnInit {

  eodModel : Eod[] | any;

  constructor(private eodService : EodService){

  }

  ngOnInit(): void {
    this.eodService.getAll().subscribe( res => {
      this.eodModel = res;
      console.log(res);
     });
  }

  onDelete(id : any){
    console.log(id);
    this.eodService.deleteEod(id).subscribe( res => {
      if( res.status == 204 ){
         this.refreshUI();
      }
    })
  }

  refreshUI(){
    this.eodService.getAll().subscribe( res => {
      this.eodModel = res;
    });
  }

}
<div class="container">
    <div class="col-sm-10"></div>
    <div>
        <h3 class="offset-sm-5 my-3">END OF DATE REPORTS</h3>
    </div>
    <hr>
    <div class="table-responsive">
        <table class="table table-primary">
            <thead>
                <tr>
                    <th>Work Type</th>
                    <th>Work Id</th>
                    <th>Title</th>
                    <th>Date</th>
                    <th>Area Path</th>
                    <th>ADO Time</th>
                    <th>Proact Time</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                <tr class="" *ngFor="let item of eodModel">
                    <td>{{item.WorkType}}</td>
                    <td>{{item.WorkId}}</td>
                    <td>{{item.Title}}</td>
                    <td>{{item.Date}}</td>
                    <td>{{item.AreaPath}}</td>
                    <td>{{item.ADOTime}}</td>
                    <td>{{item.ProactTime}}</td>
                    <td><Button routerLink="upate/:Id"><i class="bi bi-pencil-square"></i></Button>&nbsp;
                    <Button (click)="onDelete(item.Id)"><i class="bi bi-trash3-fill"></i></Button></td>
                </tr>
            </tbody>
        </table>
    </div>

</div>
