import { Component, OnInit } from '@angular/core';
import { Eod } from '../Model/eod';
import { EodService } from '../Service/eod.service';

@Component({
  selector: 'app-view-eod',
  templateUrl: './view-eod.component.html',
  styleUrls: ['./view-eod.component.css']
})
export class ViewEODComponent implements OnInit {

  eodModel : Eod[] | any;

  constructor(private eodService : EodService){
   eodService.getAll().subscribe( res => {
    this.eodModel = res;
   })
  }

  ngOnInit(): void {

  }

  onDelete(id : any){
    this.eodService.deleteEod(id).subscribe( res => {
      if( res.status == 200 ){
         this.refreshUI();
      }
    })
  }

  refreshUI(){
    this.eodService.getAll().subscribe( res => {
      this.eodModel = res;
    });
  }

}
<div class="container">
    <div class="col-sm-10"></div>
    <div>
        <h3 class="offset-sm-5 my-3">END OF DATE REPORTS</h3>
    </div>
    <hr>
    <div class="table-responsive">
        <table class="table table-primary">
            <thead>
                <tr>
                    <th scope="col">Work Id</th>
                    <th scope="col">Title</th>
                    <th scope="col">Date</th>
                    <th scope="col">Area Path</th>
                    <th scope="col">ADO Time</th>
                    <th scope="col">Proact Time</th>
                </tr>
            </thead>
            <tbody>
                <tr class="" *ngFor="let item of eodModel">
                    <td scope="row">{{item.workId}}</td>
                    <td scope="row">{{item.title}}</td>
                    <td scope="row">{{item.date}}</td>
                    <td scope="row">{{item.areaPath}}</td>
                    <td scope="row">{{item.adoTime}}</td>
                    <td scope="row">{{item.proactTime}}</td>
                </tr>
            </tbody>
        </table>
    </div>

</div>
